import React, { useState, useEffect} from 'react';
import {BrowserRouter, Routes, Route, Link} from 'react-router-dom' 

import 'styles/page/login.css';
import 'styles/responsive/reLogin.css';

import {
    signInWithEmailAndPassword,	
    onAuthStateChanged,			
    signOut,						
    getAuth,						
    updatePassword,				
  } from "firebase/auth";
  import { auth } from "../firebase";

function Login() {

    const [loginEmail, setLoginEmail] = useState("");
    const [loginPassword, setLoginPassword] = useState("");

    const [user, setUser] = useState({});
    
    const auth2 = getAuth(); 
    const currentUser = auth2.currentUser;
    // console.log(currentUser);
    
    useEffect(() => {
      onAuthStateChanged(auth, (currentUser) => { 
        setUser(currentUser);
      });
    }, [user]);
    
    console.log(user);
    
    const login = async () => {  
      try {
        const user = await signInWithEmailAndPassword(auth, loginEmail, loginPassword);
        console.log(user);
      } catch (error) {
        console.log(error.message);
      }
    };
    const logout = async () => { await signOut(auth); };   
    
    function ch_bg(){
      document.querySelector('.body').style.backgroundImage="url(img/login/login_on.jpg)"
      
    };
    
    const [emailValid, setEmailValid] = useState(false);
    const [pwValid, setPwValid] = useState(false);

    const handleEmail = (e) => {
      setLoginEmail(e.target.value);
      const regex = 
        /^(([^<>()\[\].,;:\s@"]+(\.[^<>()\[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\].,;:\s@"]{2,})$/i;
      if (regex.test(e.target.value)) {
        setEmailValid(true);
      } else {
        setEmailValid(false)
      }
    };

    const handlePw = (e) => {
      setLoginPassword(e.target.value);
      const regex = 
        /^(?=.*[a-zA-z])(?=.*[0-9])(?=.*[$`~!@$!%*#^?&\\(\\)\-_=+])(?!.*[^a-zA-z0-9$`~!@$!%*#^?&\\(\\)\-_=+]).{8,20}$/;
      if (regex.test(e.target.value)) {
        setPwValid(true);
      } else {
        setPwValid(false)
      }
    };
  return (
      <div className='body container'>
          <section className="login_box">
            <h2>반갑습니다.</h2>
              <legend className="hide">Porsche Login</legend>
              <label for="u_id" className="id_txt">Porsche ID(이메일 주소)</label>
              <input 
                type="email" 
                className="login_input" 
                id="email" 
                name="email" 
                placeholder="porsche@gmail.com" 
                value={loginEmail}
                onChange={handleEmail}/>
              <div id="err_message" className="err_txt">
                {!emailValid && loginEmail.length > 0 && (
                  <div>올바른 이메일을 입력해주세요.</div>
                )}
              </div>
              <label for="pwd" className="id_txt">암호</label>
              <input 
                type="password" 
                className="login_input" 
                id="pwd" 
                name="pwd" 
                placeholder="영문, 숫자, 특수문자 포함 8자 이상" 
                />
              <div id="err_message" className="err_txt">
                영문, 숫자, 특수문자 포함 8자 이상 입력해주세요.
              </div>
              <button clcik={true} type="submit" className="id_btn" onClick={() => {
                ch_bg()
                login();
              }}>지금 로그인</button>
            <hr className="line" />
            <p className="join_txt">
            <Link to="/join">지금 등록하세요</Link>
              , Porsche의 디지털 세상을 알아보세요.
            </p>
            <a href="#" className="forget_pwd">암호를 잊어버리셨나요?</a>
            <h4> User Logged In: </h4>
            {user?.email}
            <button onClick={logout}> Sign Out </button>
          </section>
      </div>
  );
}

export default Login;
