import React, {Component, useState, useEffect} from 'react';
import {BrowserRouter, Routes, Route, Link} from 'react-router-dom' 

import 'styles/page/login.css';

import Header from 'components/Header'
import Footer from 'components/Footer'


import {
  createUserWithEmailAndPassword,	// 🔥 1
  signInWithEmailAndPassword,		// 🔥 2
  onAuthStateChanged,				// 🔥 3
  signOut,							// 🔥 4
  getAuth,							// 🔥 5
  updatePassword,					// 🔥 6
} from "firebase/auth";
import { auth } from "./firebase";

function Login() {
  const [registerEmail, setRegisterEmail] = useState("");
  const [registerPassword, setRegisterPassword] = useState("");
  const [loginEmail, setLoginEmail] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  
  const [user, setUser] = useState({});

  const auth2 = getAuth(); // 🔥 5
  const currentUser = auth2.currentUser;
  // console.log(currentUser);
  
  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {  // 🔥 3
      setUser(currentUser);
    });
  }, [user]);

  console.log(user);


  const login = async () => {  // 🔥 2
    try {
      const user = await signInWithEmailAndPassword(auth, loginEmail, loginPassword);
      console.log(user);
    } catch (error) {
      console.log(error.message);
    }
  };

  const logout = async () => { await signOut(auth); };   // 🔥 4

  return (
      <div className='body'>
          <section className="login_box">
          <h2>반갑습니다.</h2>
          <form name="id_form" action="login_pwd.html" method="get" onsubmit="return idChk()">
              <fieldset>
                  <legend className="hide">Porsche Login</legend>
                  <label for="u_id" className="id_txt">Porsche ID(이메일 주소)</label>
                  <input type="email" className="login_input" id="email" name="email" onChange={(event) => { setLoginEmail(event.target.value); }}/>
                  <label for="pwd" className="id_txt">암호</label>
                  <input type="password" className="login_input" id="pwd" name="pwd" onChange={(event) => { setLoginPassword(event.target.value); }} />
              </fieldset>
              <p id="err_id" className="err_txt"></p>
              <button type="submit" className="id_btn" onClick={login}>지금 로그인</button>
          </form>
      
      
          <hr className="line" />
      
          <p className="join_txt">
              <Link to="join">지금 등록하세요</Link>
              , Porsche의 디지털 세상을 알아보세요.
          </p>
          <a href="#" className="forget_pwd">암호를 잊어버리셨나요?</a>
          </section>
      </div>
  );
}

export default Login;
