import React, { useState, useEffect } from 'react';
import {BrowserRouter, Routes, Route, Link} from 'react-router-dom' 

import 'styles/responsive/join.css'
import 'styles/page/join.css'

import Header from 'components/Header'
import Footer from 'components/Footer'

import {
    createUserWithEmailAndPassword,	// 🔥 1
    onAuthStateChanged,				// 🔥 3
    getAuth,							// 🔥 5
  } from "firebase/auth";
  import { auth } from "../firebase";

function Join() {

    const [registerEmail, setRegisterEmail] = useState("");
    const [registerPassword, setRegisterPassword] = useState("");
    const [user, setUser] = useState({});

    const auth2 = getAuth(); // 🔥 5
    const currentUser = auth2.currentUser;
    // console.log(currentUser);

    useEffect(() => {
        onAuthStateChanged(auth, (currentUser) => {  // 🔥 3
        setUser(currentUser);
        });
    }, [user]);

    console.log(user);

    var pwd = document.getElementById("pwd")
    const register = async () => {  // 🔥 1
        try {
          const user = await createUserWithEmailAndPassword(auth, registerEmail, registerPassword);
          console.log(user);
        } catch (error) {
          console.log(error.message);
          alert('입력하세요')
        }
      };

    //   var pwd = document.getElementById("pwd")
    //   if(!pwd.value){
    //     var err = document.getElementById("err_pwd");
    //     err.innerHTML = "\* 비밀번호를 입력하세요"
    //     pwd.focus();
    //     return false;
    // };


  return (
    <div>
        <main id="main" className="container">
            <div className="top_txt">
                <h2>Porsche ID를 받으십시오.</h2>
                <p>포르쉐 가족의 일원이 되어 Porsche ID를 만드십시오.</p>
                <p>모든 포르쉐 디지털 서비스 및 기능의 핵심입니다.</p>
            </div>
                <legend className="hide">회원정보</legend>
                <div className="form_group txt">
                    <label for="email">이메일 주소</label>
                    <input type="email" id="email" className="email"  onChange={(event) => {setRegisterEmail(event.target.value); }}/>
                    <p id="err_email" className="err_txt"></p>
                </div>
                <div className="form_group txt">
                    <label for="pwd">암호</label>
                    <input type="password" id="pwd" className="pwd" name="pwd" onChange={(event) => { setRegisterPassword(event.target.value); }}/>
                    <p id="err_pwd" className="err_txt"></p>
                </div>
                <p>Porsche ID 계정을 위한 강력한 암호를 입력하십시오. 암호에 다음을 포함시켜야 합니다.</p>
                <div className="chk_wrap">
                    <div>
                        <input type="checkbox" id="chk_all" onclick="chkAll(this)"/>
                        <label for="chk_1">전체 동의</label>
                    </div>
                    <div>
                        <input type="checkbox" id="chk_1" name="apply" />
                        <label for="chk_1">저는 이용약관의 내용을 확인하였으며, 이에 동의합니다.</label>
                    </div>
                    <div>
                        <input type="checkbox" id="chk_2" name="apply" />
                        <label for="chk_2">저는 개인정보 수집∙이용 동의서 의 내용을 확인하였으며, 이에 동의합니다.</label>
                    </div>
                    <div>
                        <input type="checkbox" id="chk_3" name="apply" />
                        <label for="chk_3">저는 개인정보 제3자 제공 동의서 의 내용을 확인하였으며, 이에 동의합니다.</label>
                    </div>
                    <div>
                        <input type="checkbox" id="chk_4" name="apply" />
                        <label for="chk_4">저는 개인정보 국외 이전 동의서 의 내용을 확인하였으며, 이에 동의합니다.</label>
                    </div>
                    <div>
                        <input type="checkbox" id="chk_5" name="apply" />
                        <label for="chk_5">저는 만 14세 이상 입니다.</label>
                    </div>
                    <p id="err_apply" className="err_txt"></p>
                </div>
                <Link to="/joinfinish"><button className="submit_btn" onClickCapture={()=> {
                    register()
                }}>Porsche ID 계정 생성</button></Link>
        </main>
    </div>
  );
}

export default Join;
